import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import 'bootstrap/dist/css/bootstrap.min.css'; // Importamos los estilos de Bootstrap
import '../styles/Login.css'; // Importamos tus estilos personalizados si los necesitas
import EmailVerification from './EmailVerification'; // Importamos el componente EmailVerification

function Login({ onLogin }) {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [showRegistrationModal, setShowRegistrationModal] = useState(false); // Estado para controlar la visibilidad del modal
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/users/login', formData);
      if (response.status === 200) {
        onLogin();
        toast.success('Inicio de sesión exitoso.');
        setTimeout(() => {
          navigate('/');
        }, 2000);
      }
    } catch (error) {
      toast.error('Error al iniciar sesión. Por favor, verifica tus credenciales.');
    }
  };

  // Función para abrir el modal de registro
  const handleRegistrationModalOpen = () => {
    setShowRegistrationModal(true);
  };

  // Función para cerrar el modal de registro
  const handleRegistrationModalClose = () => {
    setShowRegistrationModal(false);
  };

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="card p-4">
        <h2 className="text-center mb-4">Iniciar Sesión</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">Email:</label>
            <input type="email" className="form-control" id="email" name="email" value={formData.email} onChange={handleChange} required />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Contraseña:</label>
            <input type="password" className="form-control" id="password" name="password" value={formData.password} onChange={handleChange} required />
          </div>
          <button type="submit" className="btn btn-primary w-100">Iniciar Sesión</button>
        </form>
        <div className="text-center mt-3">
          <Link to="/forgot-password">¿Olvidaste tu contraseña?</Link>
        </div>
        <div className="text-center mt-3">
          {/* Botón para abrir el modal de registro */}
          <button onClick={handleRegistrationModalOpen}>Registrarse</button>
        </div>
      </div>

      {/* Modal de registro */}
      {showRegistrationModal && (
        <div className="modal-overlay">
          <div className="modal">
            {/* Botón para cerrar el modal */}
            <button onClick={handleRegistrationModalClose} className="modal-close-button">X</button>
            {/* Contenido del modal */}
            <EmailVerification onClose={handleRegistrationModalClose} />
          </div>
        </div>
      )}
    </div>
  );
}

export default Login;
