function LeftMenu({ name }) {
    return (
      <div className="bg-light border-right" id="sidebar" style={{ marginLeft: 0 }}>
        <div className="sidebar-heading">
          <FontAwesomeIcon icon={faUserCircle} className="mr-2" style={{ fontSize: '24px', color: '#1877f2' }} /> {/* Icono de foto de perfil */}
          <span className="fw-bold" style={{ color: '#1877f2' }}>{name}</span> {/* Nombre del usuario */}
        </div>
        <ul className="list-unstyled components pl-0"> {/* Agregamos la clase pl-0 aquí */}
          <li>
            <a href="/perfil" style={{ margin: '10px 0' }}> {/* Ajusta el margen superior e inferior */}
              <FontAwesomeIcon icon={faUser} className="mr-2" />
              Mi Perfil
            </a>
          </li>
          <li>
            <a href="/publicaciones" style={{ margin: '10px 0' }}> {/* Ajusta el margen superior e inferior */}
              <FontAwesomeIcon icon={faNewspaper} className="mr-2" />
              Mis Publicaciones
            </a>
          </li>
          <li>
            <a href="/mis-favoritos" style={{ margin: '10px 0' }}> {/* Ajusta el margen superior e inferior */}
              <FontAwesomeIcon icon={faHeart} className="mr-2" />
              Mis Favoritos
            </a>
          </li>
          <li>
            <a href="/cambiar-contrasena" style={{ margin: '10px 0' }}> {/* Ajusta el margen superior e inferior */}
              <FontAwesomeIcon icon={faKey} className="mr-2" />
              Cambiar Contraseña
            </a>
          </li>
          <li>
            <a href="/ayuda" style={{ margin: '10px 0' }}> {/* Ajusta el margen superior e inferior */}
              <FontAwesomeIcon icon={faQuestionCircle} className="mr-2" />
              Ayuda/Soporte
            </a>
          </li>
          <li>
            <a href="/acerca-de" style={{ margin: '10px 0' }}> {/* Ajusta el margen superior e inferior */}
              <FontAwesomeIcon icon={faInfoCircle} className="mr-2" />
              Acerca de
            </a>
          </li>
        </ul>
      </div>
    );
  }
  
  export default LeftMenu;
