import React from 'react';
import TopMenu from '../components/TopMenu';
import LeftMenu from '../components/LeftMenu';
import { useNavigate } from 'react-router-dom';

function HomePage({ onLogout, name }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Aquí puedes ejecutar la función para cerrar sesión
    onLogout(); // O la función adecuada para limpiar la información de inicio de sesión

    // Redirigir al usuario a la página de inicio de sesión
    navigate('/login');
  };

  return (
    <>
      <TopMenu onLogout={handleLogout} />
      <div className="container-fluid"> {/* Cambiamos container por container-fluid */}
        <div className="row">
          <div className="col-md-3">
            <LeftMenu name={name} /> 
          </div>
          <div className="col-md-9">
            <div className="container d-flex justify-content-center align-items-center vh-100">
              <div className="card p-4">
                <h1 className="text-center mb-4">Bienvenido a la página principal</h1>
                {/* Aquí puedes agregar el contenido de la página */}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default HomePage;
