import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import '../styles/Login.css';

function Login({ onLogin }) {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [isRegistering, setIsRegistering] = useState(false);
  const [email, setEmail] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [isVerified, setIsVerified] = useState(false);
  const [showVerificationForm, setShowVerificationForm] = useState(false);
  const [isSending, setIsSending] = useState(false);
  const [resendTimeout, setResendTimeout] = useState(120);
  const [showResendButton, setShowResendButton] = useState(false);
  const [registerData, setRegisterData] = useState({
    name: '',
    password: '',
    confirmPassword: '',
    address: ''
  });
  const [passwordMatch, setPasswordMatch] = useState(true);
  const navigate = useNavigate();

  const handleLoginChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/users/login', formData);
      if (response.status === 200) {
        onLogin();
        toast.success('Inicio de sesión exitoso.');
        setTimeout(() => {
          navigate('/');
        }, 2000);
      }
    } catch (error) {
      toast.error('Error al iniciar sesión. Por favor, verifica tus credenciales.');
    }
  };

  const handleEmailSubmit = async (e) => {
    e.preventDefault();
    setIsSending(true);
    try {
      const response = await axios.post('http://localhost:8080/api/users/sendVerificationCode', { email });
      if (response.status === 200) {
        toast.success('Se ha enviado un código de verificación a tu email.');
        // Cerrar emailModal y abrir verificationModal
        document.getElementById('emailModal').modal('hide');
        document.getElementById('verificationModal').modal('show');
        setShowVerificationForm(true);
        setResendTimeout(120);
        setShowResendButton(false);
        setVerificationCode('');
        setEmail('');
      } else {
        toast.error('Error al enviar el código de verificación.');
      }
    } catch (error) {
      toast.error('Error al enviar el código de verificación.');
    } finally {
      setIsSending(false);
    }
  };
  
  
  
  

  const handleResendCode = async (e) => {
    e.preventDefault();
    setIsSending(true);
    try {
      await axios.post('http://localhost:8080/api/users/sendVerificationCode', { email });
      toast.success('Se ha enviado un nuevo código de verificación a tu email.');
      setResendTimeout(120);
      setShowResendButton(false);
      setVerificationCode('');
    } catch (error) {
      toast.error('Error al enviar el nuevo código de verificación.');
    } finally {
      setIsSending(false);
    }
  };

  const handleVerificationSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/users/verifyCode', { email, verificationCode });
      if (response.status === 200) {
        setIsVerified(true);
        toast.success('Código verificado correctamente.');
        document.getElementById('verificationModal').modal('hide');
        document.getElementById('registrationModal').modal('show');
      } else {
        toast.error('Código de verificación incorrecto.');
      }
    } catch (error) {
      toast.error('Error al verificar el código.');
    }
  };

  useEffect(() => {
    let timer;
    if (showVerificationForm && !isVerified) {
      timer = setInterval(() => {
        setResendTimeout(prevTimeout => {
          if (prevTimeout > 0) {
            return prevTimeout - 1;
          } else {
            clearInterval(timer);
            setShowResendButton(true);
            return 0;
          }
        });
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [showVerificationForm, isVerified]);

  useEffect(() => {
    setPasswordMatch(registerData.password === registerData.confirmPassword);
  }, [registerData.password, registerData.confirmPassword]);

  const handleRegisterChange = (e) => {
    setRegisterData({ ...registerData, [e.target.name]: e.target.value });
  };
  
  const handleRegistrationSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8080/api/users/register', { ...registerData, email });
      toast.success('Registro exitoso.');
      document.getElementById('registrationModal').modal('hide');
      setTimeout(() => {
        navigate('/login');
      }, 4000);
    } catch (error) {
      toast.error('Error al registrar usuario.');
    }
  };
  
  return (
    <div className="container d-flex flex-column justify-content-center align-items-center vh-100">
      <ToastContainer />
      <div className="card p-4 w-50">
        <h2 className="text-center mb-4">Iniciar Sesión</h2>
        <form onSubmit={handleLoginSubmit}>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">Email:</label>
            <input type="email" className="form-control" id="email" name="email" value={formData.email} onChange={handleLoginChange} required />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Contraseña:</label>
            <input type="password" className="form-control" id="password" name="password" value={formData.password} onChange={handleLoginChange} required />
          </div>
          <button type="submit" className="btn btn-primary w-100">Iniciar Sesión</button>
        </form>
        <div className="text-center mt-3">
          <Link to="/forgot-password">¿Olvidaste tu contraseña?</Link>
        </div>
        <div className="text-center mt-3">
          <button onClick={() => setIsRegistering(true)} className="btn btn-link" data-bs-toggle="modal" data-bs-target="#emailModal">Registrarse</button>
       
          </div>
      </div>

      {/* Email Modal */}
      <div className="modal fade" id="emailModal" tabIndex="-1" aria-labelledby="emailModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="emailModalLabel">Ingrese su correo electrónico</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleEmailSubmit}>
                <div className="mb-3">
                  <label htmlFor="registerEmail" className="form-label">Email:</label>
                  <input type="email" className="form-control" id="registerEmail" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </div>
                <button type="submit" className="btn btn-primary w-100" disabled={isSending}>Continuar</button>
              </form>
            </div>
          </div>
        </div>
      </div>

      {/* Verification Modal */}
      <div className="modal fade" id="verificationModal" tabIndex="-1" aria-labelledby="verificationModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="verificationModalLabel">Código de verificación</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleVerificationSubmit}>
                <div className="mb-3">
                  <label htmlFor="verificationCode" className="form-label">Código de verificación:</label>
                  <input type="text" className="form-control" id="verificationCode" value={verificationCode} onChange={(e) => setVerificationCode(e.target.value)} required />
                </div>
                <button type="submit" className="btn btn-primary w-100">Verificar</button>
              </form>
              <p className="text-center mt-3">Tiempo restante: {resendTimeout} segundos</p>
              {showResendButton && (
                <button onClick={handleResendCode} className="btn btn-link w-100" disabled={isSending}>
                  Volver a Enviar Código
                </button>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Registration Modal */}
      <div className="modal fade" id="registrationModal" tabIndex="-1" aria-labelledby="registrationModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="registrationModalLabel">Registro</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleRegistrationSubmit}>
                <div className="mb-3">
                  <label htmlFor="name" className="form-label">Nombre:</label>
                  <input type="text" className="form-control" id="name" name="name" value={registerData.name} onChange={handleRegisterChange} required />
                </div>
                <div className="mb-3">
                  <label htmlFor="password" className="form-label">Contraseña:</label>
                  <input type="password" className="form-control" id="password" name="password" value={registerData.password} onChange={handleRegisterChange} required />
                </div>
                <div className="mb-3">
                  <label htmlFor="confirmPassword" className="form-label">Confirmar Contraseña:</label>
                  <input type="password" className="form-control" id="confirmPassword" name="confirmPassword" value={registerData.confirmPassword} onChange={handleRegisterChange} required />
                  {registerData.confirmPassword && (
                    <p style={{ color: passwordMatch ? 'green' : 'red' }}>
                      {passwordMatch ? 'Las contraseñas coinciden' : 'Las contraseñas no coinciden'}
                    </p>
                  )}
                </div>
                <div className="mb-3">
                  <label htmlFor="address" className="form-label">Dirección:</label>
                  <input type="text" className="form-control" id="address" name="address" value={registerData.address} onChange={handleRegisterChange} required />
                </div>
                <button type="submit" className="btn btn-primary w-100" disabled={!passwordMatch}>Registrar</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
