import React, { useState } from 'react';
import TopMenu from '../components/TopMenu';
import LeftMenu from '../components/LeftMenu';
import { useNavigate } from 'react-router-dom';
import { Container, Row, Col, Button } from 'react-bootstrap';

function HomePage({ onLogout, name }) {
  const navigate = useNavigate();
  const [showLeftMenu, setShowLeftMenu] = useState(false);
  const [showTopMenu, setShowTopMenu] = useState(false);

  const handleLogout = () => {
    onLogout();
    navigate('/login');
  };

  const handleLeftMenuClick = () => {
    setShowLeftMenu(!showLeftMenu);
  };

  const handleTopMenuClick = () => {
    setShowTopMenu(!showTopMenu);
  };

  return (
    <>
      <TopMenu onLogout={handleLogout} onMenuClick={handleTopMenuClick} />
      <LeftMenu name={name} show={showLeftMenu} />
      <Container fluid>
        <Row>
          <Col md={3} className="d-none d-md-block">
            <LeftMenu name={name} />
          </Col>
          <Col md={9} className="ml-sm-auto col-lg-10 px-md-4">
            <div className="container d-flex justify-content-center align-items-center vh-100">
              <div className="card p-4">
                <h1 className="text-center mb-4">Bienvenido a la p√°gina principal</h1>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
      <Button variant="primary" className="d-md-none fixed-bottom" onClick={handleLeftMenuClick}>
        <i className="fas fa-bars"></i>
      </Button>
    </>
  );
}

export default HomePage;
