import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function EmailVerification() {
  const [email, setEmail] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [isVerified, setIsVerified] = useState(false);
  const [showVerificationForm, setShowVerificationForm] = useState(false);
  const [message, setMessage] = useState('');
  const [isSending, setIsSending] = useState(false);
  const [resendTimeout, setResendTimeout] = useState(120);
  const [showResendButton, setShowResendButton] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    password: '',
    confirmPassword: '',
    address: '',
    userType: ''
  });
  const [passwordMatch, setPasswordMatch] = useState(true);
  const [showAlert, setShowAlert] = useState(false); // Estado para mostrar la alerta
  const navigate = useNavigate();

  const handleEmailSubmit = async (e) => {
    e.preventDefault();
    setIsSending(true);
    try {
      await axios.post('http://localhost:8080/api/users/sendVerificationCode', { email });
      setShowAlert(true);
      setMessage('Se ha enviado un código de verificación a tu email.');
      setShowVerificationForm(true);
      setResendTimeout(120);
      setShowResendButton(false);
      setVerificationCode('');
    } catch (error) {
      setShowAlert(true);
      setMessage('Error al enviar el código de verificación.');
    } finally {
      setIsSending(false);
    }
  };

  const handleResendCode = async (e) => {
    e.preventDefault();
    setIsSending(true);
    try {
      await axios.post('http://localhost:8080/api/users/sendVerificationCode', { email });
      setShowAlert(true);
      setMessage('Se ha enviado un nuevo código de verificación a tu email.');
      setResendTimeout(120);
      setShowResendButton(false);
      setVerificationCode('');
    } catch (error) {
      setShowAlert(true);
      setMessage('Error al enviar el nuevo código de verificación.');
    } finally {
      setIsSending(false);
    }
  };

  const handleVerificationSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/users/verifyCode', { email, verificationCode });
      if (response.status === 200) {
        setIsVerified(true);
        setShowAlert(true);
        setMessage('Código verificado correctamente.');
      } else {
        setShowAlert(true);
        setMessage('Código de verificación incorrecto.');
      }
    } catch (error) {
      setShowAlert(true);
      setMessage('Error al verificar el código.');
    }
  };

  useEffect(() => {
    let timer;
    if (showVerificationForm && !isVerified) {
      timer = setInterval(() => {
        setResendTimeout(prevTimeout => {
          if (prevTimeout > 0) {
            return prevTimeout - 1;
          } else {
            clearInterval(timer);
            setShowResendButton(true);
            return 0;
          }
        });
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [showVerificationForm, isVerified]);

  useEffect(() => {
    setPasswordMatch(formData.password === formData.confirmPassword);
  }, [formData.password, formData.confirmPassword]);

  const handleRegistrationSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8080/api/users/register', { ...formData, email });
      setShowAlert(true);
      setMessage('Registro exitoso.');
      setTimeout(() => {
        navigate('/login');
      }, 2000);
    } catch (error) {
      setShowAlert(true);
      setMessage('Error al registrar usuario.');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Función para cerrar la alerta después de 2 segundos
  useEffect(() => {
    const timeout = setTimeout(() => {
      setShowAlert(false);
      setMessage('');
    }, 2000);

    return () => clearTimeout(timeout);
  }, [showAlert]);

  return (
    <div>
      {showAlert && <div className="alert">{message}</div>}
      {!showVerificationForm && !isVerified && (
        <form onSubmit={handleEmailSubmit}>
          <div>
            <h2>Ingrese su correo electrónico:</h2>
            <label>Email:</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <button type="submit" disabled={isSending}>Continuar</button>
        </form>
      )}
      {showVerificationForm && !isVerified && (
        <>
          <form onSubmit={handleVerificationSubmit}>
            <div>
              <label>Código de verificación:</label>
              <input type="text" value={verificationCode} onChange={(e) => setVerificationCode(e.target.value)} required />
            </div>
            <button type="submit">Verificar</button>
          </form>
          <p>Tiempo restante: {resendTimeout} segundos</p>
          {showResendButton && (
            <button onClick={handleResendCode} disabled={isSending}>
              Volver a Enviar Código
            </button>
          )}
        </>
      )}
      {isVerified && (
        <form onSubmit={handleRegistrationSubmit}>
          <div>
            <label>Nombre:</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} required />
          </div>
          <div>
            <label>Contraseña:</label>
            <input type="password" name="password" value={formData.password} onChange={handleChange} required />
          </div>
          <div>
            <label>Confirmar Contraseña:</label>
            <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />
            {formData.confirmPassword && (
              <p style={{ color: passwordMatch ? 'green' : 'red' }}>
                {passwordMatch ? 'Las contraseñas coinciden' : 'Las contraseñas no coinciden'}
              </p>
            )}
          </div>
          <div>
            <label>Dirección:</label>
            <input type="text" name="address" value={formData.address} onChange={handleChange} required />
          </div>
          <div>
            <label>Tipo de Usuario:</label>
            <input type="text" name="userType" value={formData.userType} onChange={handleChange} required />
          </div>
          <button type="submit" disabled={!passwordMatch}>Registrar</button>
        </form>
      )}
    </div>
  );
}

export default EmailVerification;
