import React, { useState } from 'react';
import TopMenu from '../components/TopMenu';
import LeftMenu from '../components/LeftMenu';
import { useNavigate } from 'react-router-dom';

function HomePage({ onLogout, name }) {
  const navigate = useNavigate();
  const [leftMenuExpanded, setLeftMenuExpanded] = useState(false);
  const [topMenuExpanded, setTopMenuExpanded] = useState(false);

  const handleLogout = () => {
    onLogout();
    navigate('/login');
  };

  const handleLeftMenuClick = () => {
    setLeftMenuExpanded(!leftMenuExpanded);
  };

  const handleTopMenuClick = () => {
    setTopMenuExpanded(!topMenuExpanded);
  };

  return (
    <>
      <TopMenu onLogout={handleLogout} onMenuClick={handleTopMenuClick} />
      <LeftMenu name={name} onMenuClick={handleLeftMenuClick} />
      <div className="container-fluid">
        <div className="row">
          <div className={`col-md-3 ${leftMenuExpanded ? 'show' : 'hide'}`}>
            <LeftMenu name={name} onMenuClick={handleLeftMenuClick} />
          </div>
          <div className="col-md-9">
            <div className="container d-flex justify-content-center align-items-center vh-100">
              <div className="card p-4">
                <h1 className="text-center mb-4">Bienvenido a la página principal</h1>
                {/* Aquí puedes agregar el contenido de la página */}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default HomePage;
