import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useParams, useNavigate } from 'react-router-dom';

function ResetPassword() {
  const { token } = useParams();
  const navigate = useNavigate();
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [formData, setFormData] = useState({
    password: '',
    confirmPassword: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      toast.error('Las contraseñas no coinciden.', {
        autoClose: 3000,
      });
      return;
    }

    try {
      await axios.post(`http://localhost:8080/api/users/reset-password/${token}`, { newPassword: password });
      toast.success('Contraseña restablecida correctamente.', {
        autoClose: 3000,
      });
      setTimeout(() => {
        navigate('/login');
      }, 3000);
    } catch (error) {
      toast.error('Error al restablecer la contraseña.', {
        autoClose: 3000,
      });
    }
  };
  useEffect(() => {
    setPasswordMatch(formData.password === formData.confirmPassword);
  }, [formData.password, formData.confirmPassword]);  

  return (
    <div>
      <h2>Restablecer Contraseña</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nueva Contraseña:</label>
          <input
            type="password"
            name="password"
            value={formData.password} onChange={handleChange} required
          />
        </div>
        <div>
          <label>Confirmar Nueva Contraseña:</label>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword} onChange={handleChange}
            required
          />
          {formData.confirmPassword && (
              <p style={{ color: passwordMatch ? 'green' : 'red' }}>
                {passwordMatch ? 'Las contraseñas coinciden' : 'Las contraseñas no coinciden'}
              </p>
            )}
        </div>
        <button type="submit" disabled={!passwordMatch}>Restablecer Contraseña</button>
      </form>
    </div>
  );
}

export default ResetPassword;
