import React from 'react';

function TopMenu({ onLogout }) {
  const handleLogout = () => {
    onLogout();
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container justify-content-center"> {/* Contenedor centrado */}
        <a className="navbar-brand" href="/">
          <i className="fas fa-home"></i> {/* Icono de inicio */}
          {/* También puedes usar: <i className="fa fa-home"></i> para versiones anteriores de Font Awesome */}
          Logo
        </a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item">
              <a className="nav-link" href="/">
                <i className="fas fa-search"></i> {/* Icono de búsqueda */}
                Inicio
              </a>
            </li>
          </ul>
          <form className="d-flex">
            <input className="form-control me-2" type="search" placeholder="Buscar" aria-label="Buscar" />
            <button className="btn btn-outline-primary" type="submit">
              <i className="fas fa-search"></i> {/* Icono de búsqueda */}
              {/* También puedes usar: <i className="fa fa-search"></i> para versiones anteriores de Font Awesome */}
            </button>
          </form>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a className="nav-link" href="/explorar">
                <i className="fas fa-globe"></i> {/* Icono de explorar */}
                Explorar
              </a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="/configuracion">
                <i className="fas fa-cog"></i> {/* Icono de configuración */}
                Configuración
              </a>
            </li>
            <li className="nav-item">
              <button className="btn btn-danger" onClick={handleLogout}>
                <i className="fas fa-sign-out-alt"></i> {/* Icono de cerrar sesión */}
                Cerrar Sesión
              </button>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default TopMenu;
