import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/Login.css';
import { toast } from 'react-toastify'; // Importa toast de react-toastify

function Login({ onLogin }) {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const navigate = useNavigate();

  useEffect(() => {
    // Envía un mensaje al iframe padre indicando que el formulario se ha cargado
    parent.postMessage('loginFormLoaded', '*');
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/users/login', formData);
      if (response.status === 200) {
        onLogin();
        toast.success('Inicio de sesión exitoso.'); // Muestra notificación de éxito
        setTimeout(() => {
          navigate('/');
        }, 2000);
      }
    } catch (error) {
      toast.error('Error al iniciar sesión. Por favor, verifica tus credenciales.'); // Muestra notificación de error
    }
  };

  return (
    <div className="login-form">
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Contraseña:</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange} required />
        </div>
        <button type="submit">Iniciar Sesión</button>
      </form>
      <Link to="/forgot-password">¿Olvidaste tu contraseña?</Link>
    </div>
  );
}

export default Login;
