import React from 'react';

function LeftMenu({ userFullName }) {
  return (
    <div className="bg-light border-right" id="sidebar" style={{ marginLeft: 0 }}>
      <div className="sidebar-heading">
        <i className="fas fa-user-circle mr-2"></i> {/* Icono de foto de perfil */}
        {userFullName} {/* Nombre del usuario */}
      </div>
      <ul className="list-unstyled components pl-0"> {/* Agregamos la clase pl-0 aquí */}
        <li>
          <a href="/perfil">Mi Perfil</a>
        </li>
        <li>
          <a href="/publicaciones">Mis Publicaciones</a>
        </li>
        
        <li>
          <a href="/mis-favoritos">Mis Favoritos</a>
        </li>
        <li>
          <a href="/cambiar-contrasena">Cambiar Contraseña</a>
        </li>
        
        <li>
          <a href="/ayuda">Ayuda/Soporte</a>
        </li>
        <li>
          <a href="/acerca-de">Acerca de</a>
        </li>
      </ul>
    </div>
  );
}

export default LeftMenu;
