import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';

function GetEmail() {
  const [email, setEmail] = useState('');
  const [redirectToVerification, setRedirectToVerification] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Verificar si el correo electrónico es válido antes de redirigir
    if (emailIsValid(email)) {
      setRedirectToVerification(true);
    } else {
      alert('Por favor, ingrese un correo electrónico válido.');
    }
  };

  // Función para verificar si el correo electrónico es válido
  const emailIsValid = (email) => {
    // Expresión regular para validar el formato del correo electrónico
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    // Verifica si el correo electrónico coincide con la expresión regular
    return emailRegex.test(email);
  };

  return (
    <div>
      {redirectToVerification && <Navigate to="/email-verification" />}
      <h2>INGRESA TU CUENTA DE GMAIL PARA PROCEDER CON EL REGISTRO</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </label>
        <button type="submit">Continuar</button>
      </form>
    </div>
  );
}

export default GetEmail;
