//EmailVerification.js
import React, { useState } from 'react';
import axios from 'axios';

function EmailVerification() {
  const [verificationCode, setVerificationCode] = useState('');
  const [verificationError, setVerificationError] = useState('');
  const [verificationSuccess, setVerificationSuccess] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/users/verifyCode', { verificationCode });
      if (response.data.success) {
        setVerificationSuccess(true);
      } else {
        setVerificationError('Código de verificación incorrecto');
      }
    } catch (error) {
      setVerificationError('Error al verificar el código');
    }
  };

  return (
    <div>
      <h1>Email Verification Page</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Código de Verificación:
          <input
            type="text"
            value={verificationCode}
            onChange={(e) => setVerificationCode(e.target.value)}
          />
        </label>
        <button type="submit">Verificar</button>
        {verificationError && <p style={{ color: 'red' }}>{verificationError}</p>}
        {verificationSuccess && <p style={{ color: 'green' }}>Código de verificación correcto</p>}
      </form>
    </div>
  );
}

export default EmailVerification;
