//GetEmail.jsx
import { useState } from 'react';
import { Navigate } from 'react-router-dom';

function GetEmail() {
  const [email, setEmail] = useState('');
  const [redirectToVerification, setRedirectToVerification] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí podrías validar el correo electrónico antes de redirigir
    setRedirectToVerification(true);
  };

  return (
    <div>
      {redirectToVerification && <Navigate to="/email-verification" />}
      <h1>INGRESA TU CUENTA DE GMAIL PARA PROCEDER CON EL REGISTRO</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </label>
        <button type="submit">Continuar</button>
      </form>
    </div>
  );
}

export default GetEmail;
