import React, { useState,useEffect } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';

function EmailVerification() {
  const [email, setEmail] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [isVerified, setIsVerified] = useState(false);
  const [showVerificationForm, setShowVerificationForm] = useState(false);
  const [message, setMessage] = useState('');
  const [isSendingEmail, setIsSendingEmail] = useState(false);
  const [timeLeft, setTimeLeft] = useState(120); 

  const [formData, setFormData] = useState({
    name: '',
    password: '',
    address: '',
    userType: ''
  });

  const handleEmailSubmit = async (e) => {
    e.preventDefault();
    setIsSendingEmail(true); 
    try {
      await axios.post('http://localhost:8080/api/users/sendVerificationCode', { email });
      setMessage('Se ha enviado un código de verificación a tu email.');
      setShowVerificationForm(true);
      startTimer();
    } catch (error) {
      setMessage('Error al enviar el código de verificación.');
    }finally {
      setIsSendingEmail(false); 
    }
  };
  const startTimer = () => {
    const intervalId = setInterval(() => {
      setTimeLeft(prevTimeLeft => prevTimeLeft - 1);
    }, 1000);
    return intervalId;
  };
  useEffect(() => {
    const intervalId = startTimer();
    return () => clearInterval(intervalId); 
  }, []);

  const handleVerificationSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/users/verifyCode', { email, verificationCode });
      if (response.data.success) {
        setIsVerified(true);
        setMessage('Código verificado correctamente.');
      } else {
        setMessage('Código de verificación incorrecto.');
      }
    } catch (error) {
      setMessage('Error al verificar el código.');
    }
  };

  const handleRegistrationSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8080/api/users/register', { ...formData, email });
      setMessage('Registro exitoso.');
    } catch (error) {
      setMessage('Error al registrar usuario.');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Función para verificar si el correo electrónico es válido
  const emailIsValid = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  return (
    <div>
      {!showVerificationForm && !isVerified && (
        <form onSubmit={handleEmailSubmit}>
          <div>
            <h2>Ingrese su correo electronico:</h2>
            <label>Email:</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <button type="submit" disabled={isSendingEmail}>Continuar</button>
        </form>
      )}
      {showVerificationForm && !isVerified && (
        <form onSubmit={handleVerificationSubmit}>
          <div>
            <label>Código de verificación:</label>
            <input type="text" value={verificationCode} onChange={(e) => setVerificationCode(e.target.value)} required />
          </div>
          <button type="submit">Verificar</button>
        </form>
      )}
      {isVerified && (
        <form onSubmit={handleRegistrationSubmit}>
          <div>
            <label>Nombre:</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} required />
          </div>
          <div>
            <label>Contraseña:</label>
            <input type="password" name="password" value={formData.password} onChange={handleChange} required />
          </div>
          <div>
            <label>Dirección:</label>
            <input type="text" name="address" value={formData.address} onChange={handleChange} required />
          </div>
          <div>
            <label>Tipo de Usuario:</label>
            <input type="text" name="userType" value={formData.userType} onChange={handleChange} required />
          </div>
          <button type="submit">Registrar</button>
        </form>
      )}
      {message && <p>{message}</p>}
    </div>
  );
}

export default EmailVerification;
